import './App.css';
import React, { useEffect, useState } from 'react';
import { Button, Space, Modal, Form, Input, DatePicker, Select } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import axios from 'axios';
import moment from 'moment';
import EditableTable from './EditableTable'
import internshipFormm from './InternshipForm';
const dateFormat = "MM/DD/YYYY";
const { Option } = Select;

const Intern = () => {

  const [visible, setVisible] = useState(false);
  const { TextArea } = Input;
  const [internData, setInternData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/internships").then((response) => {
      setInternData(response.data.map((val, index) => {
        return { key: val.key, name: val.name, date: moment(val.date.toString().substring(0, 10), "YYYY-MM-DD"), status: val.status, notes: val.notes }
      }))
    });
  }, [])

  const onFinish = (values) => {
    handleOk();
    const newData = {
      key: 0,
      name: values.name,
      date: values.date,
      status: values.status,
      notes: values.notes,
    }
    axios.post("http://localhost:3001/create", newData).then(() => {
      axios.get("http://localhost:3001/internships").then((response) => {
        setInternData(response.data.map((val, index) => {
          return { key: val.key, name: val.name, date: moment(val.date.toString().substring(0, 10), "YYYY-MM-DD"), status: val.status, notes: val.notes }
        }));
      })
    });
  };



  const [modalText, setModalText] = useState(internshipForm);

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('Submitted!');
    setTimeout(() => {
      setVisible(false);
    }, 500);
  };

  const handleCancel = () => {
    setVisible(false);
  };

  const handleNew = () => {
    setModalText(internshipForm);
    showModal();
  };

  return (
    <div className="buttons">
      <Button onClick={handleNew}>
        <Space>
          <PlusOutlined />
        </Space>
      </Button>
      <EditableTable internData={internData} setInternData={setInternData} />
      <Modal
        title="Add Internship"
        visible={visible}
        onCancel={handleCancel}
        footer={null}
      >
        {modalText}
      </Modal>
    </div>
  );

}

export default Intern;
