import './App.css';
import React from 'react'
import { useState } from 'react';
import { Button, Dropdown, Space, Menu, Table, Modal, Form, Input } from 'antd';
import { PlusOutlined } from '@ant-design/icons';

function App() {
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState(false);
  const [confirmLoading, setConfirmLoading] = useState(false);

  const onFinish = (values) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };

  const internshipForm = (<Form
    name="basic"
    labelCol={{
      span: 8,
    }}
    wrapperCol={{
      span: 16,
    }}
    initialValues={{
      remember: true,
    }}
    onFinish={onFinish}
    onFinishFailed={onFinishFailed}
    autoComplete="off"
  >
    <Form.Item
      label="Username"
      name="username"
      rules={[
        {
          required: true,
          message: 'Please input your username!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      wrapperCol={{
        offset: 8,
        span: 16,
      }}
    >
      <Button type="primary" htmlType="submit">
        Submit
      </Button>
    </Form.Item>
  </Form>);

  const [modalText, setModalText] = useState(internshipForm);

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true);
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };

  const handleNew = () => {
    showModal();
    const newData = {
      name: "n",
      date: "d",
      status: "s",
      notes: "no",
    }
    setData(
      pre => { return [...pre, newData] }
    )
  };
  const menu = (
    <Menu
      onClick={handleNew}
      items={[
        {
          label: 'new internship',
          key: '1',
          icon: <PlusOutlined />,
        },
      ]}
    />
  );
  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: 'Notes',
      dataIndex: 'notes',
      key: 'notes',
    }
  ];
  return (
    <div>
      <div className="App-header">
        <Dropdown overlay={menu}>
          <Button>
            <Space>
              <PlusOutlined />
            </Space>
          </Button>
        </Dropdown>
        <Table columns={columns} dataSource={data} />
        <Modal
          title="Title"
          visible={visible}
          onOk={handleOk}
          confirmLoading={confirmLoading}
          onCancel={handleCancel}
        >
          <p>{modalText}</p>
        </Modal>
      </div>
    </div>
  );
}

export default App;
