import './App.css';
import React, { useState } from 'react';
import { Button, Dropdown, Space, Menu, Table } from 'antd';
import { PlusOutlined } from '@ant-design/icons';

function App() {
  const [data, setData] = useState([]);
  const menu = (
    <Menu
      items={[
        {
          label: 'new internship',
          key: '1',
          icon: <PlusOutlined />,
        },
      ]}
    />
  );
  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: 'Notes',
      dateIndex: 'notes',
      key: 'notes',
    }
  ];
  const handleNew = () => {
    const newData = {
      name: "n",
      date: "d",
      status: "s",
      notes: "none"
    }
    setData(
      pre => { return [...pre, newData] }
    )
  };
  return (
    <div>
      <div className="App-header">
        <Dropdown overlay={menu}>
          <Button onClick={handleNew}>
            <Space>
              <PlusOutlined />
            </Space>
          </Button>
        </Dropdown>
        <Table columns={columns} data={data} />
      </div>
    </div>
  );
}

export default App;
