import React, { useState } from "react";
import moment from "moment";
import ReactDOM from "react-dom";
import "antd/dist/antd.css";
import "./index.css";
import { Table, Popconfirm, Form, Typography, DatePicker, Space, Tooltip } from "antd";
const originData = [];

for (let i = 0; i < 2; i++) {
  originData.push({
    key: i.toString(),
    //name: "name" + i.toString(),
    date: moment("2012-01-11T12:32:26.551Z", moment.ISO_8601),
    //status: "New",
    //notes: "none"
  })
}

const EditableCell = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  ...restProps
}) => {
  const dateFormat = "MM/DD/YYYY";
  console.log(record);
  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name="date"
          style={{
            margin: 0
          }}
          rules={[
            {
              required: true,
              message: `Please Input ${title}!`
            }
          ]}
        >
          <DatePicker

            format={dateFormat}
          />
        </Form.Item>
      ) : (
        children
      )}
    </td>
  );
};

const EditableTable = () => {
  const [form] = Form.useForm();
  const [data, setData] = useState(originData);
  const [editingKey, setEditingKey] = useState("");

  const isEditing = (record) => record.key === editingKey;

  const edit = (record) => {
    form.setFieldsValue({
      name: '',
      date: '',
      status: '',
      notes: '',
      ...record
    });
    setEditingKey(record.key);
  };

  const cancel = () => {
    setEditingKey("");
  };

  const save = async (key) => {
    try {
      const row = await form.validateFields();
      const newData = [...data];
      const index = newData.findIndex((item) => key === item.key);

      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        setData(newData);
        setEditingKey("");
      } else {
        newData.push(row);
        setData(newData);
        setEditingKey("");
      }
    } catch (errInfo) {
      console.log("Validate Failed:", errInfo);
    }
  };
  //   const deleteInternship = (record) => {
  //     setData(pre => {
