  //         <Popconfirm title="Are you sure?" okText="Yes" cancelText="No" onConfirm={() => deleteInternship(record)}>
  //           <DeleteOutlined style={{ fontSize: '20px', color: 'red', marginLeft: '12px' }} />
  //         </Popconfirm>
  //       </div>
  //     );
  //   }
  const columns = [
    // {
    //   title: 'Name',
    //   dataIndex: 'name',
    //   sorter: (a, b) => { if (a.name < b.name) return -1; if (a.name > b.name) return 1; return 0; },
    //   key: '1',
    //   editable: true
    // },
    {
      title: 'Date',
      dataIndex: 'date',
      key: '2',
      render: (_, record) => new Date(record.date._i).toLocaleDateString('en-US'),
      editable: true
    },
    // {
    //   title: 'Status',
    //   dataIndex: 'status',
    //   key: '3',
    //   editable: true
    // },
    // {
