import './App.css';
import React from 'react'
import { useState } from 'react';
import { Button, Space, Table, Modal, Form, Input, Popconfirm, Tooltip } from 'antd';
import { PlusOutlined, DeleteOutlined, EditOutlined, CheckOutlined, CloseOutlined } from '@ant-design/icons';
//import Intern from './Intern';
//import Network from './Network';

function App() {

  const Network = () => {
    const [contactData, setContactData] = useState([
      {
        key: 0,
        name: 'n',
        email: 'e',
        phone: 'p',
        notes: 'nn',
      }
    ]);
    const [visible, setVisible] = useState(false);
    const { TextArea } = Input;
    const [key, setKey] = useState(0);
    const [form] = Form.useForm();
    const [editingKey, setEditingKey] = useState('');

    const EditableCell = ({
      editing,
      dataIndex,
      title,
      inputType,
      record,
      index,
      children,
      ...restProps
    }) => {
      const inputNode = dataIndex === 'notes' ? <TextArea /> : <Input />;
      const req = dataIndex === 'notes' ? false : true;
      return (
        <td {...restProps}>
          {editing ? (
            <Form.Item
              name={dataIndex}
              style={{
                margin: 0,
              }}
              rules={[
                {
                  required: req,
                  message: `Please input ${title}!`,
                },
              ]}
            >
              {inputNode}
            </Form.Item>
          ) : (
            children
          )}
        </td>
      );
    };

    const isEditing = (record) => record.key === editingKey;

    const edit = (record) => {
      form.setFieldsValue({
        name: '',
        age: '',
        address: '',
        ...record,
      });
      setEditingKey(record.key);
    };

    const cancel = () => {
      setEditingKey('');
    };

    const save = async (key) => {
      try {
        const row = await form.validateFields();
        const newData = [...contactData];
        const index = newData.findIndex((item) => key === item.key);

        if (index > -1) {
          const item = newData[index];
          newData.splice(index, 1, { ...item, ...row });
          setContactData(newData);
          setEditingKey('');
        } else {
          newData.push(row);
          setContactData(newData);
          setEditingKey('');
        }
      } catch (errInfo) {
        console.log('Validate Failed:', errInfo);
      }
    };

    const onFinish = (values) => {
      handleOk();
      const newData = {
        key: key,
        name: values.name,
        email: values.email,
        phone: values.phone,
        notes: values.notes,
      }
      setKey(key + 1);
      setContactData(
        pre => { return [...pre, newData] }
      )
    };

    const networkForm = (<Form
      name="basic"
      labelCol={{
        span: 4,
      }} s
      wrapperCol={{
        span: 16,
      }}
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      autoComplete="off"
    >
      <Form.Item
        label="Name"
        name="name"
        rules={[
          {
            required: true,
            message: 'Please input a name!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Email"
        name="email"
        rules={[
          {
            required: false,
            message: 'Please input an email!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Phone"
        name="phone"
        value="phone"
        rules={[
          {
            required: false,
            message: 'Please input a phone number!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Notes"
        name="notes"
      >
        <TextArea rows={4} />
      </Form.Item>

      <Form.Item
        wrapperCol={{
          offset: 4,
          span: 16,
        }}
      >
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>);

    const [modalText, setModalText] = useState(networkForm);

    const showModal = () => {
      setVisible(true);
    };

    const handleOk = () => {
      setModalText('Submitted!');
      setTimeout(() => {
        setVisible(false);
      }, 500);
    };

    const handleCancel = () => {
      setVisible(false);
    };

    const handleNew = () => {
      setModalText(networkForm);
      showModal();
    };
    const deleteContact = (record) => {
      setContactData(pre => {
        if (pre != null) {
          return (pre.filter((it) => it.key !== record.key))
        };
      })
    }
    const operationsCol = (_, record) => {
      const editable = isEditing(record);
      return editable ? (
        <span>
          <CheckOutlined
            onClick={() => save(record.key)}
            style={{
              marginRight: 8,
              fontSize: '20px',
              color: 'green'
            }}
          />
          <Popconfirm title="Discard changes?" onConfirm={cancel}>
            <CloseOutlined style={{ fontSize: '20px', color: 'red' }} />
          </Popconfirm>
        </span>
      ) : (
        <div>
          <Space size="large">
            <EditOutlined disabled={editingKey !== ''} onClick={() => edit(record)} style={{ fontSize: '20px', color: '#08c' }} />
          </Space>
          <Popconfirm title="Are you sure?" okText="Yes" cancelText="No" onConfirm={() => deleteContact(record)}>
            <DeleteOutlined style={{ fontSize: '20px', color: 'red', marginLeft: '12px' }} />
          </Popconfirm>
        </div>
      );
    }
    const columns = [
      {
        title: 'Name',
        dataIndex: 'name',
        key: '1',
        editable: true
      },
      {
        title: 'Email',
        dataIndex: 'email',
        key: '2',
        editable: true
      },
      {
        title: 'Phone',
        dataIndex: 'phone',
        key: '3',
        editable: true
      },
      {
        title: 'Notes',
        dataIndex: 'notes',
        key: '4',
        ellipsis: true,
        editable: true,
        render: (address => (
          <Tooltip placement="topLeft" title={address}>
            {address}
          </Tooltip>)),
      },
      {
        title: '',
        key: '5',
        render: operationsCol,
      }
    ];


    const mergedColumns = columns.map((col) => {
      if (!col.editable) {
        return col;
      }

      return {
        ...col,
        onCell: (record) => ({
          record,
          inputType: col.dataIndex === 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: isEditing(record),
        }),
      };
    });


    return (
      <div className="buttons">
        <Button onClick={handleNew}>
          <Space>
            <PlusOutlined />
          </Space>
        </Button>
        <Form form={form} component={false}>
          <Table
            components={{
              body: {
                cell: EditableCell,
              },
            }}
            bordered
            dataSource={contactData}
            columns={mergedColumns}
            rowClassName="editable-row"
            pagination={{
              onChange: cancel,
            }}
          />
        </Form>
        <Modal
          title="Add Contact"
          visible={visible}
          onCancel={handleCancel}
          footer={null}
        >
          {modalText}
        </Modal>
      </div>
    );

  }

  const Intern = () => {
    const [internData, setInternData] = useState([]);
    const [visible, setVisible] = useState(false);
    const { TextArea } = Input;
    const [key, setKey] = useState(0);
    const [form] = Form.useForm();
    const [editingKey, setEditingKey] = useState('');

    const EditableCell = ({
      editing,
      dataIndex,
      title,
      inputType,
      record,
      index,
      children,
      ...restProps
    }) => {
      const inputNode = dataIndex === 'notes' ? <TextArea /> : <Input />;
      const req = dataIndex === 'notes' ? false : true;
      return (
        <td {...restProps}>
          {editing ? (
            <Form.Item
              name={dataIndex}
              style={{
                margin: 0,
              }}
              rules={[
                {
                  required: req,
                  message: `Please input ${title}!`,
                },
              ]}
            >
              {inputNode}
            </Form.Item>
          ) : (
            children
          )}
        </td>
      );
    };

    const isEditing = (record) => record.key === editingKey;

    const edit = (record) => {
      form.setFieldsValue({
        name: '',
        age: '',
        address: '',
        ...record,
      });
      setEditingKey(record.key);
    };

    const cancel = () => {
      setEditingKey('');
    };

    const save = async (key) => {
      try {
        const row = await form.validateFields();
        const newData = [...internData];
        const index = newData.findIndex((item) => key === item.key);

        if (index > -1) {
          const item = newData[index];
          newData.splice(index, 1, { ...item, ...row });
          setInternData(newData);
          setEditingKey('');
        } else {
          newData.push(row);
          setInternData(newData);
          setEditingKey('');
        }
      } catch (errInfo) {
        console.log('Validate Failed:', errInfo);
      }
    };

    const onFinish = (values) => {
      handleOk();
      const newData = {
        key: key,
        name: values.name,
        date: values.date,
        status: values.status,
        notes: values.notes,
      }
      setKey(key + 1);
      setInternData(
        pre => { return [...pre, newData] }
      )
    };

    const internshipForm = (<Form
      name="basic"
      labelCol={{
        span: 4,
      }} s
      wrapperCol={{
        span: 16,
      }}
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      autoComplete="off"
    >
      <Form.Item
        label="Name"
        name="name"
        rules={[
          {
            required: true,
            message: 'Please input a name!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Date"
        name="date"
        rules={[
          {
            required: true,
            message: 'Please input a date!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Status"
        name="status"
        value="status"
        rules={[
          {
            required: true,
            message: 'Please input a status!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Notes"
        name="notes"
      >
        <TextArea rows={4} />
      </Form.Item>

      <Form.Item
        wrapperCol={{
          offset: 4,
          span: 16,
        }}
      >
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>);

    const [modalText, setModalText] = useState(internshipForm);

    const showModal = () => {
      setVisible(true);
    };

    const handleOk = () => {
      setModalText('Submitted!');
      setTimeout(() => {
        setVisible(false);
      }, 500);
    };

    const handleCancel = () => {
      setVisible(false);
    };

    const handleNew = () => {
      setModalText(internshipForm);
      showModal();
    };
    const deleteInternship = (record) => {
      setInternData(pre => {
        if (pre != null) {
          return (pre.filter((it) => it.key !== record.key))
        };
      })
    }
    const operationsCol = (_, record) => {
      const editable = isEditing(record);
      return editable ? (
        <span>
          <CheckOutlined
            onClick={() => save(record.key)}
            style={{
              marginRight: 8,
              fontSize: '20px',
              color: 'green'
            }}
          />
          <Popconfirm title="Discard changes?" onConfirm={cancel}>
            <CloseOutlined style={{ fontSize: '20px', color: 'red' }} />
          </Popconfirm>
        </span>
      ) : (
        <div>
          <Space size="large">
            <EditOutlined disabled={editingKey !== ''} onClick={() => edit(record)} style={{ fontSize: '20px', color: '#08c' }} />
          </Space>
          <Popconfirm title="Are you sure?" okText="Yes" cancelText="No" onConfirm={() => deleteInternship(record)}>
            <DeleteOutlined style={{ fontSize: '20px', color: 'red', marginLeft: '12px' }} />
          </Popconfirm>
        </div>
      );
    }
    const columns = [
      {
        title: 'Name',
        dataIndex: 'name',
        key: '1',
        editable: true
      },
      {
        title: 'Date',
        dataIndex: 'date',
        key: '2',
        editable: true
      },
      {
        title: 'Status',
        dataIndex: 'status',
        key: '3',
        editable: true
      },
      {
        title: 'Notes',
        dataIndex: 'notes',
        key: '4',
        ellipsis: true,
        editable: true,
        render: (address => (
          <Tooltip placement="topLeft" title={address}>
            {address}
          </Tooltip>)),
      },
      {
        title: '',
        key: '5',
        render: operationsCol,
      }
    ];


    const mergedColumns = columns.map((col) => {
      if (!col.editable) {
        return col;
      }

      return {
        ...col,
        onCell: (record) => ({
          record,
          inputType: col.dataIndex === 'age' ? 'number' : 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: isEditing(record),
        }),
      };
    });


    return (
      <div className="buttons">
        <Button onClick={handleNew}>
          <Space>
            <PlusOutlined />
          </Space>
        </Button>
        <Form form={form} component={false}>
          <Table
            components={{
              body: {
                cell: EditableCell,
              },
            }}
            bordered
            dataSource={internData}
            columns={mergedColumns}
            rowClassName="editable-row"
            pagination={{
              onChange: cancel,
            }}
          />
        </Form>
        <Modal
          title="Add Internship"
          visible={visible}
          onCancel={handleCancel}
          footer={null}
        >
          {modalText}
        </Modal>
      </div>
    );

  }






  const [intern, setIntern] = useState(true);
  const Display = () => {
    return intern ? <Intern /> : <Network />
  }

  return (
    <div>
      <div className="App-header">
        <div className="tabs">
          <Button onClick={() => setIntern(true)} style={{ width: "50%", border: "solid #e0e0e0 0.5px" }}> Internship</Button>
          <Button onClick={() => setIntern(false)} style={{ flexGrow: 1, textAlign: "center", verticalAlign: "center", border: "solid #e0e0e0 0.5px" }}>Network</Button>
        </div>
        <Display />
      </div>
    </div>
  );
}

export default App;
