import './App.css';
import React from 'react'
import { useState } from 'react';
import { Button, Dropdown, Space, Menu, Table, Modal, Form, Input } from 'antd';
import { PlusOutlined, DeleteOutlined, EditOutlined } from '@ant-design/icons';

function App() {
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState(false);
  const { TextArea } = Input;

  const onFinish = (values) => {
    handleOk();
    const newData = {
      name: values.name,
      date: values.date,
      status: values.status,
      notes: values.notes,
    }
    setData(
      pre => { return [...pre, newData] }
    )
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };

  const internshipForm = (<Form
    name="basic"
    labelCol={{
      span: 4,
    }}
    wrapperCol={{
      span: 16,
    }}
    initialValues={{
      remember: true,
    }}
    onFinish={onFinish}
    onFinishFailed={onFinishFailed}
    autoComplete="off"
  >
    <Form.Item
      label="Name"
      name="name"
      rules={[
        {
          required: true,
          message: 'Please input a name!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      label="Date"
      name="date"
      rules={[
        {
          required: true,
          message: 'Please input a date!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      label="Status"
      name="status"
      rules={[
        {
          required: true,
          message: 'Please input a status!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      label="Notes"
      name="notes"
    >
      <TextArea rows={4} />
    </Form.Item>

    <Form.Item
      wrapperCol={{
        offset: 4,
        span: 16,
      }}
    >
      <Button type="primary" htmlType="submit">
        Submit
      </Button>
    </Form.Item>
  </Form>);

  const [modalText, setModalText] = useState(internshipForm);

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('Submitted!');
    setTimeout(() => {
      setVisible(false);
    }, 500);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };

  const handleNew = () => {
    setModalText(internshipForm);
    showModal();
  };
  const menu = (
    <Menu
      onClick={handleNew}
      items={[
        {
          label: 'new internship',
          key: '1',
          icon: <PlusOutlined />,
        },
      ]}
    />
  );
  const deleteInternship = async (record) => {
    setData(pre => {
      if (pre != null)
        return pre.filter((it) => it.name !== record.name);
    })
  }
  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: '1',
    },
    {
      title: 'Date',
      dataIndex: 'date',
      key: '2',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: '3',
    },
    {
      title: 'Notes',
      dataIndex: 'notes',
      key: '4',
      ellipsis: true,
    },
    {
      title: '',
      key: '5',
      render: (row) => (
        <Space size="large">
          <EditOutlined style={{ fontSize: '20px', color: '#08c' }} />
          <Space />
          <DeleteOutlined onClick={deleteInternship(row)} style={{ fontSize: '20px', color: 'red' }} />
        </Space>
      ),
    }
  ];
  return (
    <div>
      <div className="App-header">
        <Dropdown overlay={menu}>
          <Button>
            <Space>
              <PlusOutlined />
            </Space>
          </Button>
        </Dropdown>
        <Table class="columns" columns={columns} dataSource={data} />
        <Modal
          title="Title"
          visible={visible}
          onCancel={handleCancel}
          footer={null}
        >
          <p>{modalText}</p>
        </Modal>
      </div>
    </div>
  );
}

export default App;
